   set juminb = #{juminb}
 where email = #{id}

mapper.select:
select email
  from userinfo 
 where name    = #{name}
   and teltype = #{teltype}
   and mobile  = #{mobile}
   and jumina  = #{jumina}
   and sex     = #{sex}

